import styled from 'styled-components';
import { InputBtn } from './TodoInput';
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const ListContainer = styled.ul`
  position: relative;
  background-color: #fff;
  width: 100%;
  height: 80%;
  padding: 3%;
`

const List = styled.li`
  display: flex;
  align-items: center;
  list-style: none;
  font-size: 20px;
  font-weight: 800;

  .list {
    border-radius: 10px;
    box-shadow: 5px 5px 5px #909DD9;
    width: 40%;
    padding: 20px;
    margin: 0 30px 10px 0;
    text-align: center;
  }

  .bin:hover {

  }
`

export function TodoList({todos, setTodos}){

  return (
    <>
      <ListContainer>
        {todos.map(todo => {
        return (<List key={todo.id}>
          <div className='list'>
            <span>{todo.todo}</span>
          </div>
          <FontAwesomeIcon className='bin' icon={faTrash} />
          </List>)
        })}
        <InputBtn todos={todos} setTodos={setTodos}/>
      </ListContainer>
    </>
  )
}
