import styled from 'styled-components';
import { InputBtn } from './TodoInput';
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const ListContainer = styled.ul`
  position: relative;
  background-color: #fff;
  width: 100%;
  height: 80%;
  padding: 3%;
`

const List = styled.li`
  list-style: none;
  border: 1px solid none;
  border-radius: 10px;
  box-shadow: 5px 5px 5px #909DD9;
  width: 40%;
  padding: 20px;
  margin-bottom: 10px;
  text-align: center;

  .todo_content{
    font-size: 20px;
    font-weight: 700;
  }
`

export function TodoList({todos, setTodos}){

  return (
    <>
      <ListContainer>
        {todos.map(todo => {
    return (<List key={todo.id}>
              <span className='todo_content'>{todo.todo}</span>
            </List>)
        })}
        <FontAwesomeIcon icon="fa-solid fa-trash" />
        <InputBtn todos={todos} setTodos={setTodos}/>
      </ListContainer>
    </>
  )
}
