import styled from 'styled-components';
import { useState } from 'react';

const CreateBtn = styled.button`
  position: absolute;
  right: 30px;
  bottom: 40px;

  width: 80px;
  height: 80px;
  border-radius: 50%;
  border: none;
  background-color: #909DD9;

  .add {
    color: #fff;
    font-size: 50px;
    font-weight: 500;
  }

  &:hover {
    background-color: #b8c1eb;
  }
`
const InputContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  bottom: 15px;
  right: 15px;
  width : 300px;
  height: 300px;
  padding: 20px 5px;
  border-radius: 10px;
  background-color: #909DD9;
  z-index: 1; 

  .add_title {
    color: #fff;
    font-size: 1.7rem;
    font-weight: 800;
  }

  .form {
    display: flex;
    flex-direction: column;
    width: 100%;
    margin: 15px 10px;
  }
`
const TodoInput = styled.input`
  border: none;
  border-radius: 10px;
  width: 240px;
  padding: 10px;
  margin-left: 10px;
  outline: none;
`

const SubmitBtn = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50px;
  border: none;
  background-color: #fff;

  .check {
    color: #909DD9;
  }

  &:hover {
    background-color: #33c632;

    .check {
      color: #fff;
    }
  }
`

export function InputBtn({todos, setTodos}){
  const [content, setContent] = useState('');

  const handleChange = (e) => {
    setContent(e.target.value);
  }

  const handleSubmit = (todo) => {
    const addTodo = {
      id: todos.length+1,
      todo,
      checked: false, 
    };

    setTodos({...todos, addTodo});
  }

  return (
    <>
      <InputContainer>
        <span className='add_title'>Add ToDo 😉</span>
        <form className='form'>
          <TodoInput 
          placeholder='오늘의 할일은?'
          value={content}
          onChange={handleChange}
          autoFocus
          />
          <SubmitBtn>
            <span className='check'> ✔ </span>
          </SubmitBtn>
        </form>
      </InputContainer>

      <CreateBtn>
        <span className='add'>+</span>
      </CreateBtn>
    </>
  )
}
