import styled from 'styled-components';
import { useState } from 'react';

const CreateBtn = styled.button`
  position: absolute;
  right: 30px;
  bottom: 40px;

  width: 80px;
  height: 80px;
  border-radius: 50%;
  border: none;
  background-color: #909DD9;

  .add {
    color: #fff;
    font-size: 50px;
    font-weight: 500;
  }

  &:hover {
    background-color: #b8c1eb;
  }
`
const InputContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  bottom: 15px;
  right: 15px;
  width : 350px;
  height: 350px;
  padding: 10px;
  border-radius: 10px;
  background-color: #909DD9;
  z-index: 1; 

  .add_title {
    color: #fff;
    font-size: 1.7rem;
    font-weight: 800;
  }
`
const TodoInput = styled.input`

`

export function InputBtn(){
  const [content, setContent] = useState('');
  const handleChange = (e) => {
    setContent(e.target.value);
  }

  return (
    <>
      <InputContainer>
        <span className='add_title'>Add ToDo ðŸ˜‰</span>
        <form>
          <TodoInput value={content}
          onChange={handleChange}
          />
        </form>
      </InputContainer>

      <CreateBtn>
        <span className='add'>+</span>
      </CreateBtn>
    </>
  )
}
